<?php

class Annonce
{
	public $titre;
	public $description;
	public  $image;
	public $prix;

	public function __construct( $tit, $desc, $img, $prix )
	{
		$this->titre = $tit;
		$this->description = $desc;
		$this->image = $img;
		$this->prix = $prix;
		

	}

	public function show()
	{
		echo "<h2>".$this->titre."</h2>\n";
		echo "<h3>".$this->description."</h3>\n";
		echo "<img src='".$this->image."' width='400' height='250'</>"."<br>\n";
		echo "<h3>".$this->prix."â‚¬"."</h3>\n";
		echo "<br>";
	}
}

class bateau extends Annonce
{
	private $longueur;

	public function __construct( $tit, $desc, $img, $longueur, $prix )
	{
		parent::__construct( $tit, $desc, $img, $prix );
		$this->longueur = $longueur;
		$this->prix = $prix;
	}

	public function show()
	{
		
		parent::show();
		echo "<h4>longueur :".$this->longueur."M</h4><br>\n";
	
	}
}

